void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    uv.y-=.25;

    float health = sin(iTime*.1)*.5+.5;

    float wiggle=sin(iTime*40.)*(pow(1.-health,8.5))*.02;
    float c=cos(wiggle);
    float s=sin(wiggle);
    uv*=mat2(c,-s,s,c);
    uv.x-=wiggle*.25;

    float beat = .3*(1.-pow(abs(sin(iTime*2.5)),15.))+.3;
    beat *= .6*(1.-pow(abs(sin(.8+iTime*2.5)),15.))+.3;
    float r = length(uv)*6.5-beat*health;    

    uv = normalize(uv);
    float pct = r - ((uv.y*pow(abs(uv.x),0.6))/(uv.y+1.5)-(2.)*uv.y+1.26);

    pct = 1.0-smoothstep(.48,0.5,pct);
    vec3 color = pct*vec3(.12+health,.15,.2);
	fragColor = vec4( color, 1.0 );
}
