#if 0                                    // integer version 

  #define hash(v)  float( int (v) % 289 )
  #define hashV(v) vec2( ivec2(v) % 289 )

#else                                    // slight difference in the float version

  #define hash(v)  mod(  floor(v), 289.)
  #define hashV(v) hash(v)

#endif

// Added wrongness on OpenGL, not showing on Windows where 
// signed mod or % differs from OpenGL + silent abs() are added here and there.
// This cause x to grow up to 1e9 on OpenGL for uv.x<0 instead of "just" 1e5 on Windows.
// â†’ fract on ~ 1e5 only have 1-2 bits remaining.
// past 1e7, fract = 0 : there are no longer any remaining bits.
// But here even on Windows it is a problem:
// Note the vertical bands in red: indeed, U.y influence was in the lowest bits.

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U =  10.* ( 2.*u - R ) / R.y,

         p = hashV(U);
    float x = (34. * p.x + 1.) * hash(p.x) + p.y;
    x = (34. * x + 1.) * hash(x);
    x /= 41.;

    O = U.y > 0. 
          ?  vec4( fract(x) )           // top: random value
          : vec4(x/5e5,0,0,1);          // red: show x content

    if ( U.y < -8. ) 
        O = vec4( fract(1e7*u.x/R.x) ); // bottom: show fract( 0 ... 1e7 )
}
