float DistLine(vec3 ro, vec3 rd, vec3 p)
{
    return length(cross(p-ro,rd))/length(rd);
}

float CircleMask3D(float size, vec3 p, vec3 ro, vec3 rd)
{
    float d = DistLine(ro,rd,p);
    float blur = 3./iResolution.y;
    return smoothstep(size, size - blur, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    //Ray
    vec3 ro = vec3(.0, .0, -2.);
    vec3 rd = vec3(uv.x, uv.y, .0) - ro;

    //point position
    float x = .8 * cos(iTime);
    float y = .8 * sin(iTime);
    float z = 2. + 5. * (sin(iTime * .2 + 345.4) * .5 + .5);
    float circle = CircleMask3D(.5, vec3(x, y, z), ro, rd);

    float d = DistLine(ro,rd,vec3(x, y , z));
    vec3 col = vec3(circle) * d;
    //col = vec3(d);

    fragColor = vec4(col,1.0);
}
