#define BW 0 // set to 1 to make the output black and white
// set to 0 to make it a mix of cyan, red, black, and white

float hill_val(in vec2 uv, in vec3 selector) {
  uv = mod(uv, vec2(1.0));
  float val = dot(selector, texture(iChannel0, uv).rgb) - 0.5;
  return val;
}

vec2 hill_grad(in vec2 uv, in vec3 selector) {
  float f = hill_val(uv, selector);
  return 1000.0 * vec2(hill_val(uv + vec2(0.001, 0.0), selector) - f,
                       hill_val(uv + vec2(0.0, 0.001), selector) - f);
}

float coverage(in vec2 uv, in vec3 selector) {
    float val = hill_val(uv, selector);
    float grad_mag = length(hill_grad(uv, selector)/iResolution.xy);

    float to_zero = val / grad_mag;

    float cov = max(0.0, min(0.5 + to_zero, 1.0));
    return cov;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float cyan = coverage(uv, vec3(1.0, 0.0, 0.0));
    float red = coverage(uv, vec3(0.0, 1.0, 0.0));

#if BW
    cyan = red;
#endif

    vec3 col = vec3(red, cyan, cyan);
    // Output to screen
    fragColor = vec4(col,1.0);
}

const float diffuse_weight = 0.025; // higher means less diffusion.  set greater than 0 and less than 1
const float react_weight = 0.25; // reaction speed : don't exceed 0.25.  higher is faster reaction

// in the function "greatest" there are two calls to "color_to_val_4"
// change those to "color_to_val_1", "color_to_val_2" etc to get different effects

vec2 to_uv(in vec2 in_pixels) {
    return in_pixels / iResolution.xy;
	// return 0.1 + mod(vec2(0.8) + in_pixels / iResolution.xy, vec2(0.9));
}




void mainImage( out vec4 fragColor, in vec2 p )
{

    vec4 color = texture(iChannel1, to_uv(p));

    // color.rg = mod(10.0 * to_uv(p), vec2(1.0));
    color.b = 1.0 - 0.5 * color.r - 0.5 * color.g;


    if (iTime < 0.05 || texelFetch(iChannel3, ivec2(32, 0), 0).r > 0.01) {
	    fragColor = vec4(color.rgb, 1.0);
        return;
    }


    color = diffuse_weight * texture(iChannel0, to_uv(p));

    vec2 dir = 0.25 * normalize(mod(to_uv(p), vec2(0.5)) - vec2(0.25));
      // vec2(0.95 * smoothstep(0.05, 0.0, abs(mod(to_uv(p).x, 0.25) - 0.125)), 0.0);
      // vec2(0.0, 0.95);
    mat2 diffuse_tensor = mat2(1.0 - dir.x * dir.x, -dir.x * dir.y,
                               -dir.x*dir.y, 1.0 - dir.y * dir.y);

    float w = 0.25 * (1.0 - diffuse_weight);
    const float scale = 2.0;
    color = color +  w * texture(iChannel0, to_uv(p + scale * diffuse_tensor * vec2(1.1, 0.0)));
    color = color +  w * texture(iChannel0, to_uv(p + scale * diffuse_tensor * vec2(0.0, 1.1)));
    color = color +  w * texture(iChannel0, to_uv(p - scale * diffuse_tensor * vec2(1.1, 0.0)));
    color = color +  w * texture(iChannel0, to_uv(p - scale * diffuse_tensor * vec2(0.0, 1.1)));

    color.rgb = color.rgb + react_weight * (1.0 * color.rgb * color.gbr - color.rgb * color.brg);//  + 0.005;

    color.a = 1.0;
    color.rgb = max(vec3(0.0), min(vec3(1.0), color.rgb));

    float delta = 1.5 - dot(color.rgb, vec3(1.0));

    color.rgb += 0.01 * delta;


    fragColor = vec4(color.rgb,1.0);
}
