mat2 Rot (vec2 v) {
    float s = sin(v.x), c = cos(v.y);
    return mat2(c, -s, s, c);
}

vec2 rotate(vec2 _uv, float _angle){
    _uv -= 0.5;
    _uv =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _uv;
    _uv += 0.5;
    return _uv;
}

float box(vec2 _uv, vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size,_size+vec2(0.01),_uv);
    uv *= smoothstep(_size,_size+vec2(0.01), vec2(1.0)-_uv);
    return uv.x*uv.y;
}

vec2 tile(vec2 _uv, float _zoom){
    _uv *= _zoom;
    return fract(_uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);

    vec2 gv = tile(uv, 4.);      
    col = vec3(box(gv, vec2(.9)));

    gv = rotate(gv, 3.141592*0.25);
    col += vec3(box(gv+vec2(sin(iTime), 0.), vec2(1.)));

    fragColor = vec4(col,1.0);
}
