vec3 red = vec3(0.894,0.118,0.039);
vec3 lightblue = vec3(0.384,0.725,0.914);
vec3 blue = vec3(0.18,0.137,0.51);


float RoundBox(vec2 p, float size, float curve) {
 p = abs(mod(p, 1.0) - 0.5) + size * (curve - 0.5);
 return smoothstep(0., 1.0 / iResolution.y, length(max(p, 0.)) - size * curve);
}

void mainImage(out vec4 oFragColor, in vec2 iFragCoord) {
  float f = 0.9;

  vec2 p = iFragCoord / iResolution.y;
  float r = RoundBox(
    p + 0.13 * iTime, 
    0.6 + 0.2 * cos(1.3 * iTime),
    0.1 + 0.1 * sin(0.4 * iTime)
  );
  float b = RoundBox(
    p + vec2(-0.1 * iTime, 0.5 + 0.1 * iTime), 
    0.6 + 0.2 * sin(1.1 * iTime), 
    0.1 + 0.1 * sin(0.7 * iTime)
  );

  vec3 c = mix(mix(lightblue, mix(red, vec3(1.0), r), b), blue, 1.0 - r - b);
  oFragColor += vec4(c, 1.0);
}
