void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord-iResolution.xy/2.0;
    mat2 rotate = mat2(cos(iTime),sin(iTime),-sin(iTime),cos(iTime));

    float min_len = min(iResolution.x,iResolution.y)/2.0;
    vec3 black = vec3(0);
    vec3 white = vec3(1);
    vec3 col;

    vec2 uv_normal = fragCoord/iResolution.xy;

    uv = rotate * uv;

    vec2 radius = vec2(3.0*min_len/8.0,1.0*min_len/8.0);
    if(length(uv) > radius.x * 2.0){
        col = vec3(uv_normal.xy,0.5+0.5*cos(iTime/2.));
        fragColor = vec4(col,1.0);
        return;
    }

    vec2 center1 = vec2(radius.x,0.0);
    vec2 center2 = vec2(-radius.x,0.0);


    if(uv.y > 0.0){
        col = black;
    }
    else col = white;

    if(length(uv-center1) < radius.x) col = black;
    if(length(uv-center2) < radius.x) col = white;
    if(length(uv-center1) < radius.y) col = white;
    if(length(uv-center2) < radius.y) col = black;


    // Output to screen
    fragColor = vec4(col,1.0);
}
