void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float width = 12.0;
    vec2 screenCoord = fragCoord - iResolution.xy/3.0;
    vec2 uv =  width * screenCoord / min(iResolution.x, iResolution.y);

    float x = uv.x;
    float y = uv.y;
    float z = iTime*1.0;
    float d = sin(x)*cos(y) + sin(y)*cos(z) + sin(z)*cos(x);
    // diamond
    //d = sin(x)*sin(y)*sin(z)+sin(x)*cos(y)*cos(z)+cos(x)*sin(y)*cos(z)+cos(x)*cos(y)*sin(z);
    //Schwartz
    //d = cos(x)+cos(y)+cos(z);

    float h = (d + 1.0)/2.0;
    float l = 0.0;

    if(0.45<h&&h<0.55){l=1.0;};

    // Output to screen
    fragColor = vec4(vec3(l),1.0);
}
