// Interleaved Gradient Noise as per https://blog.demofox.org/2022/01/01/interleaved-gradient-noise-a-different-kind-of-low-discrepancy-sequence/
float ign(ivec2 p) {
    return fract(52.9829189 * fract(dot(vec2(p), vec2(0.06711056, 0.00583715))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(ign(ivec2(fragCoord / 50.0  + iTime)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
