void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //for easier reading
    vec2 m = iMouse.xy;
    vec2 res = iResolution.xy;
    vec2 xy = fragCoord.xy;

    vec2 uv = xy/res.xy-.5;
    float k = res.x/res.y;
    uv.x *= k;

    float a = length(uv.x)*length(uv.y);
    if(a<abs(m.x/res.x-.5))
    {
        a = abs(m.x/res.x-.5) - a;
        fragColor = vec4(a/fwidth(a));
    }
}
