void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color =vec4(1.0,0.0,0.0,1.0);
    vec2 screen = fragCoord.xy / iResolution.xy;
    vec4 texColor = texture (iChannel0, screen);
    //texColor.b = screen.x;
    texColor.r *= abs(sin(iTime)* screen.x);
    texColor.g *= abs(cos(iTime)*screen.y);
    fragColor = texColor;
}
