float band(float t, float start, float end, float blur){
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);

    return step1*step2;
}
float circle(vec2 uv, vec2 p, float r, float blur){
    float d = length(uv - p);
    float c = smoothstep(r, r - blur, d);
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy -.5*iResolution.xy)/iResolution.y;
    vec2 pol = vec2(atan(uv.x, uv.y), length(uv)); //polar coordinates

    //horizontal sine waves
    /*
    float a = uv.y + 0.1*sin(iTime+uv.x*20.);
    float b = a + 0.1*sin(iTime+uv.x*150.)*smoothstep(-.3, .5, uv.x);
    float col = band(b, -0.01, 0.01, 0.001);//*/

    //circular sine waves
    /*
    float a2 = -0.4 + pol.y + 0.025*sin(iTime+pol.x*20.);
    float b2 = a2 + 0.025*sin(iTime+pol.x*150.)*sqrt(smoothstep(-2., 2., pol.x))*sqrt(smoothstep(2., -.5, pol.x));
    float col2 = band(b2, -0.005, 0.005, 0.001);//*/

    //distorted circular sine waves
    float a2 = -0.4 + pol.y + 0.025*sin(iTime+pol.x*20.);
    float b2 = a2 + 0.025*sin(iTime+pol.x*150.*(pol.y+0.1))*sqrt(smoothstep(-2., 2., pol.x))*sqrt(smoothstep(2., -.5, pol.x));
    float col2 = band(b2, -0.005, 0.005, 0.001);

    col2 += band(b2+.1, -0.01, 0.01, 0.001);
    col2 += band(b2+.2, -0.015, 0.015, 0.001);
    col2 += band(b2+.3, -0.02, 0.02, 0.001);

    fragColor = vec4(2.*col2*uv.x, 2.*col2*(-uv.x), 2.*col2*abs(uv.y), 1.);
    //fragColor = vec4(vec3(col2*(uv.x+.5), col2*(uv.y+.5), col2*(uv.x*uv.y+.5)), 1.);
    //fragColor = vec4(uv.x*uv.y);
}
