void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = 2.5*(fragCoord-.5*iResolution.xy )/iResolution.y;
  vec2 m = 2.5*(iMouse.xy-.5*iResolution.xy )/iResolution.y;
  vec2 z = uv.xy;
  vec3 col = vec3(0.);
  float a = .1;
  for(int i=0; i<128; i++)
  {
      a *= 1.1;
      z = vec2(z.x*z.x*z.x-3.*z.x*z.y*z.y, 3.*z.x*z.x*z.y-z.y*z.y*z.y)+m;
        if(length(z)>4.)
            {
                col = vec3(a,a*m.x,a*m.y); break;
            }
        else
            col = vec3(1.);
  }

      fragColor = vec4(col,1.);
}
