void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float brightness = 0.0;
    vec2 uv = abs(fragCoord / iResolution.xy - vec2(0.5));

    float d = sqrt(uv.x * uv.x + uv.y * uv.y);
    float m = d > 0.35 ? 1.0 : -1.0;
    if (mod(uv.x + uv.y + iTime * 0.5 * m, 0.05) > 0.025)
        brightness = 1.0;

    fragColor = vec4(vec3(brightness), 1.0);
    if (d < 0.01) fragColor = vec4(1.0, 0.0, 0.0, 1.0);
}
