const float maxRad = 0.433012702,
            vs = 0.866025404,
            scaling = 10.;

vec2 nearestHexCenter(in vec2 coord){
    vec2 nearest = 
                    vec2(
                         floor(coord.x) + (1. + 2.*floor(mod(coord.x, 1.) - 0.5)) * floor(mod(coord.y / vs, 2.)) * 0.5,
                         floor(coord.y / vs) * vs
                        )
                  + vec2(.5, vs * 0.5);
    return nearest;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/min(iResolution.x, iResolution.y);


    vec2 coord = uv * scaling;
    float distPerPix = scaling / min(iResolution.x, iResolution.y);
    float val = 0.5 * (1. + sin(iTime + uv.x * 5.));
    vec3 col = mix(vec3(0.776,0.776,0.776), vec3(0.024,0.596,1.000),
               smoothstep(val * maxRad - distPerPix, val * maxRad,
                          distance(coord, nearestHexCenter(coord))
                          )
                   );

    // Output to screen
    fragColor = vec4(col,1.0);
}
