void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ouv = uv;
    uv.y -= 0.5;
    float z = 20.;
    uv *= z;

    float result = 0.;
    float a = sin(uv.x + iTime)*3.;
    float b = sin(uv.y + iTime)*3.;

    if(ouv.x < 0.5){
        if(ouv.y < 0.5){
            // left bottom
            uv.y += 0.25*z;
            fragColor.g = 0.2;
        }else{
            // left top
            uv.y -= 0.25*z;
            a = a / b;
        }
    }else if(ouv.x > 0.5){
        if(ouv.y < 0.5){
            // right bottom
            uv.y += 0.25*z;
            a = a + b;
        }else{
            // right top
            uv.y -= 0.25*z;
            a = a * b;
            fragColor.g = 0.2;
        }
    }
    result = step(a, uv.y) - step(a+.5, uv.y);

    // Output to screen
    fragColor.r = result;
}
