float h21(vec2 st){
    return fract(sin(dot(st,vec2(213.25,47.288))+56.32)*5827.56);
}
float gerp(float a,float b,float t){
    return a*pow(b/a,t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 st = 3.* ( fragCoord - .5*iResolution.xy ) / iResolution.y;
    float t=iTime;

    float d=mix(length(st-.7*vec2(cos(.15*t-.1),sin(.15*t-.1))),abs(dot(st,st)-1.),.4);
    float flow=1./(1.+d*d);
    float col1=pow(h21(st),.4)*smoothstep(h21(st),flow,.8+sin(-1.262+t*.47)*.05)*pow(smoothstep(0.,1.,flow),.3);

    float d2=gerp(length(st-.6*vec2(cos(.15*t),sin(.15*t))),abs(dot(st,st)-1.),.4);
    float flow2=1./(1.+d2*d2);
    float col2=h21(st)*smoothstep(h21(st),flow2,.75+sin(t*.5)*.07)*pow(smoothstep(0.,1.,flow2),7.5+.8*sin(t*.7));

    vec3 color=vec3(gerp(col1,col2,.47));

    fragColor=vec4(color,1.);
}
