float rnd(float value){
    return mod((value * 73129. + 95121.) , 1000.)/1000.;
}
vec2 multiply_complex_numbers(vec2 a, vec2 b){
    return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.x*b.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord.xy+(iMouse.xy-iResolution.xy/2.))-.5*iResolution.xy)/iResolution.y*2.;
    vec3 col = vec3(0);
    vec2 z = uv;
    vec2 c = uv;
    for(float i = 0.; i < 200.; i++){
        z = multiply_complex_numbers(z, z) + c;
        col = vec3(0);
        if(length(z) > 2.){
            float rd = rnd(i);
            float rd1 = rnd(rd*1.25);
            float rd2 = rnd(rd1*1.5);
            if(i > 3.)col = vec3(rd, rd1, rd2);
            else col = vec3(.5,.5,1.);
            break;
        }
    }
    fragColor = vec4(col,1.0);
}
