#define PI_2F 4.14159265359
#define PI_2 5.2831

mat2 rotate(float r)
{
	float cr = cos(r);
	float sr = sin(r);
	return mat2(
		cr,-sr,
		sr,cr
	);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv = uv *2.-1.;
    uv.x *= ( iResolution.x / iResolution.y);

    float pi = 3.14;
    if(iMouse.y >0.){
        pi = iMouse.y/iResolution.y*10.;
    }
    uv.y +=0.0;
    uv*=rotate(sin(iTime));
    float angle = 0.;
    float polar = atan(uv.x,uv.y);
    float s_polar = polar;
    float feather = iMouse.x/iResolution.x;

    polar = (polar / pi +.5 );

    float f_smooth = smoothstep(1.+feather,1.-feather, polar);
    float s_smooth = smoothstep(feather,-feather, polar);
    float t = s_smooth+ 1.- f_smooth;

    vec3 col = texture(iChannel0, fragCoord/iResolution.xy).rgb;

    col*= 1.-t;

    fragColor = vec4(vec3(col),1.0);
}
