void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   vec4 color = vec4(0);
   float ab = 0.002;
   vec2 uv = fragCoord.xy/iResolution.xy;
   if (uv.x > 0.333 && uv.x< 0.666){
        //Aberration 
        ab = 0. + iMouse.x/iResolution.x *1.;
        ab += 0.002;
        float _distance = distance(uv,vec2(2.0));
        color.r += texture(iChannel0,uv + (_distance*fract(ab))).r;
        color.g += texture(iChannel0,uv).g;
        color.b += texture(iChannel0,uv - (_distance*fract(ab))).b;
        fragColor = color;
   } else fragColor = texture(iChannel0, uv);

   //Lines
   if (uv.x > 0.333 && uv.x < 0.334)
       fragColor = vec4(0.);

   if (uv.x > 0.666 && uv.x < 0.667)
       fragColor = vec4(0.);

}
