// Triple32 Hash: https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17U;
    x *= 0xED5AD4BBU;
    x ^= x >> 11U;
    x *= 0xAC4C1B51U;
    x ^= x >> 15U;
    x *= 0x31848BABU;
    x ^= x >> 14U;
    return x;
}

// Random Value Between 0.0 and 1.0
float rand()
{
    ns = triple32(ns);
    return float(ns)/float(0xFFFFFFFFU);
}

