// Random Uniform Direction
vec2 udir2()
{
    float z = rand();
    float r = 2.0*pi*z;
    float s = sin(r), c = cos(r);
    return vec2(s, c);
}

vec3 udir3()
{
    vec2 z = rand2();
    vec2 r = vec2(2.0*pi*z.x, acos(2.0*z.y-1.0));
    vec2 s = sin(r), c = cos(r);
    return vec3(c.x*s.y, s.x*s.y, c.y);
}

