    vec2 k4 = texture(iChannel3, (fragCoord - h * k3)       /iResolution.xy).xy;
    uv -= h * (k1 + 2.0 * k2 + 2.0 * k3 + k4) / 6.0;
    uv /= iResolution.xy;

