void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    if(texture(iChannel0, uv).z > 0.0)
    {
        return;
    }

    float x0 = texture(iChannel0, (vec2(-1.0,  0.0)+fragCoord)/iResolution.xy).x;
    float x1 = texture(iChannel0, (vec2( 1.0,  0.0)+fragCoord)/iResolution.xy).x;
    float y0 = texture(iChannel0, (vec2( 0.0, -1.0)+fragCoord)/iResolution.xy).y;
    float y1 = texture(iChannel0, (vec2( 0.0,  1.0)+fragCoord)/iResolution.xy).y;

    float divergence = 0.5*( (x1-x0)+(y1-y0) );

    fragColor = vec4(divergence, 0.0, 0.0, 0.0);
}

