float samplePressure(vec2 p)
{
    if(texture(iChannel0, p).z > 0.5)
    {
        return 0.0;
    }

    if(p.x < 0.01 || p.x > 0.99 || p.y < 0.01 || p.y > 0.99)
    {
        return 0.0;
    }

    return texture(iChannel2, p).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float divergence = texture(iChannel1, uv).x;

    float x0 = samplePressure( (vec2(-1.0,  0.0)+fragCoord)/iResolution.xy );
    float x1 = samplePressure( (vec2( 1.0,  0.0)+fragCoord)/iResolution.xy );
    float y0 = samplePressure( (vec2( 0.0, -1.0)+fragCoord)/iResolution.xy );
    float y1 = samplePressure( (vec2( 0.0,  1.0)+fragCoord)/iResolution.xy );

    fragColor = vec4(0.25*( (x0+x1+y0+y1)-divergence ), 0.0, 0.0, 0.0);
}

