void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float x0 = texture(iChannel2, (vec2(-1.0,  0.0)+fragCoord)/iResolution.xy).x;
    float x1 = texture(iChannel2, (vec2( 1.0,  0.0)+fragCoord)/iResolution.xy).x;
    float y0 = texture(iChannel2, (vec2( 0.0, -1.0)+fragCoord)/iResolution.xy).x;
    float y1 = texture(iChannel2, (vec2( 0.0,  1.0)+fragCoord)/iResolution.xy).x;

    vec2 pressureGradient = 0.5*( vec2(x1, y1)-vec2(x0, y0) );

    vec2 velocity = texture(iChannel0, uv).xy;

    fragColor = vec4(velocity-pressureGradient, 0.0, 0.0);
