void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y, P;

    float t = mod(iDate.w,60.),
          s = 2.*fract(t+.5)-1.;
    s = s < 0. ? s += 1.145, s*=s*s, s = -s*s*(3.-2.*s)       // mad ease function. before tick
               : 8.*sin(25.*s)/(1.+1e2*s);                    //                    after tick
    P = .7*sin( (floor(t)+s)*.10471976 + vec2(0,33) );        // needle location

    O = 1.-R.yyyy/4.*min( abs( length(U) - .8 ) -.03,         // circle
                          length( U - P * clamp( dot(U,P)/dot(P,P), 0.,1.) ) -.01 ); // needle
}

vec2 mainSound( int s, float t )
{
    return vec2( fract( t + fract(iDate.w) ) );
}
