#define PI 3.14159265
#define STEP 12

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

float angle(vec2 uv, float i) {
    float inc = i / float(STEP) * PI * 2.;
    float time = sin(2. * iTime + i * .12) * .5 + .5;
    uv.x += sin(inc) * time * .45;
    uv.y += cos(inc) * time * .45;
    uv = rotate(uv, -inc);
    float c = smoothstep(-PI * float(STEP), PI, atan(uv.x, uv.y));
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(1.);    
    for (int i = 0; i < STEP; i++) {
        col *= angle(uv, float(i));
    }
    col = smoothstep(0.6, .9, col);
    col = mix(vec3(1., 0., 0.), vec3(1.0, .9, 0.), col.r);

    fragColor = vec4(col,1.0);
}
