#define ITERS 12
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    float t=(iTime)/16.0;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/10.0;
    uv.y += t;
    for(int c=0;c<ITERS;c++){
        float scale = 2.25;
        float scale1 = 1.9;
        float s1 = scale1*scale;
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {
            uv = sin(uv.yx/s1)*s1;
            uv= -fract(uv+((vec2(uv.x/scale-uv.y/scale1,uv.y/scale-uv.x/scale1)/(scale))))*scale/scale1;
            uv.y /= -scale1;
            scale1 += (uv.x*(.005*fract(uv.x+t*s1)));
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev.yzx))/2.0;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}
