void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = ((2.0 * fragCoord - iResolution.xy) / iResolution.y) * 1.5;
    uv.x -= 0.5;
    vec2 z = vec2(0.0);
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + uv;
        if(dot(z, z) > 65536.0) break;
    }
    l = l - log2(log2(dot(z,z))) + 4.0; // This is the only line you need

    // When using smooth iterations ^,
    // You must set the interior of the fractal to your max iterations
    // if you want the interior to be solid and not black. See below.

    if(dot(z, z) < 65536.0) l = 100.0;
    fragColor = vec4(vec3(sqrt(l / 100.0)) * vec3(0.4, 1.0, 1.3), 1.0);
}
