const float xSize = 0.02;

float point (float f) {
    return xSize/(abs(f)+xSize);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = texture(iChannel0, uv);
    vec4 grayColor = vec4(dot(color.rgb, vec3(0.299, 0.587, 0.114)));
    grayColor.a = 1.0;

    float progress = mod(iTime*0.5, 1.0);
    float f = abs(uv.x - progress);
    vec4 col = vec4(vec3(point(f)),1.0);


    // Output to screen
    fragColor = mix(color, grayColor, step(progress, uv.x)) + col;
}
