#define PI 3.14159265358979323846

vec3 screen(vec3 base, vec3 bl_layer, float alpha){
    return base + alpha*(bl_layer - base * bl_layer);
}

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

vec2 scale2D(vec2 _st, vec2 _scale){
    _st -= 0.5;
    _st = mat2(_scale.x,0.0,
               0.0,_scale.y) * _st;
    _st += 0.5;
    return _st;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 tex0 = texture(iChannel0, scale2D(uv, vec2(0.9)));
    vec4 tex1 = texture(iChannel1, uv);
    tex1.r = texture(iChannel1, scale2D(uv, vec2(0.9)) + 0.05*sin(iTime)).r;
    tex1.b = texture(iChannel1, scale2D(uv, vec2(0.9)) - 0.05*sin(iTime)).b;

    // Output to screen
    float alpha = 0.95;
    fragColor = vec4(screen(tex0.rgb, tex1.rgb, alpha), 1.);
}
