float st = 50.;
float tw = 2.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = vec2(uv.x+sin(length(uv-.5)*st+iTime)*(uv.y-.5)*tw/(st*length(uv-.5)+1.),uv.y-sin(length(uv-.5)*st+iTime)*(uv.x-.5)*tw/(st*length(uv-.5)+1.));

    vec3 col = texture(iChannel0,uv).xyz;

    fragColor = vec4(col,1.);
}
