void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float foob = iTime * .5;
    float myTime = (int(foob) % 2 == 0 ? fract(foob) : 1.0 - fract(foob));

    vec2 loc1 = vec2(myTime, .67);
    if (distance(uv, loc1) < .01)
        col = vec3(1);

    vec2 loc2 = vec2(smoothstep(0.0, 1.0, myTime), .33);
    if (distance(uv, loc2) < .01)
        col = vec3(1);

    // Output to screen
    fragColor = vec4(col,1.0);
}
