void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_tiles = 20.0;

    float x = fragCoord.x / iResolution.x * n_tiles;
    float y = fragCoord.y / iResolution.x * n_tiles;
    // Stripe
    vec3 color = vec3(0.0,0.0,1.0);
    if (bool(mod(floor(x),2.0)))
        color = vec3(1.0,1.0,0.0);
    // Grid
    if (bool(mod(floor(y),2.0)))
        color = 1.0 - color;
    // Output to screen
    fragColor = vec4(color,1.0);
}

