float hash( float n ){
    return fract(sin(n)*43758.5453);
}
float smin(float a, float b, float k) {
  float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
  return mix(a, b, h) - k*h*(1.0-h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mousePos = (iMouse.xy - iResolution.xy*.5) / iResolution.y;
    vec2 p = (fragCoord - iResolution.xy*.5) / iResolution.y;

    float time = iTime / 6.0;

    float colOut = 1.0;

    for (float i = 0.0; i < 32.0; i += 1.0) {

        //float rnd = i * 3.141 * 2.0 + 0.412;

        //vec2 pos = vec2(sin(iTime / 2.0 * 0.9212 + rnd * 0.6512), cos(iTime / 2.0 * 1.0442 + rnd * 0.1264)) / 3.0;
        float rnd = i*(3.141 * 2.0 + .4123);
		vec2 pos = vec2(sin(time*0.95 + rnd*3.131)*0.5, cos(time*1.05 + rnd*5.763)*0.4) * 2.0;
		pos *= vec2(sin(time*1.05 + i*i*3.141/7.131), cos(time*.95 + i*(i-1.0)*3.141/4.235))*1.25;


        float dist = distance(p, pos);
        colOut = smin(colOut, dist, 0.1);
    }

    vec3 col = vec3(colOut);

    if (colOut <= 0.14) {

        if (colOut <= 0.09) {
            col = vec3(0.44, 0.1, 0.36);
        } else {
            col = vec3(0.93, 0.5, 0.8);
        }

    } else {
        float c = (1.0-colOut) * 0.9;
        col = vec3(c * 0.5, c / 2.0, c * 0.8);
    }



    fragColor = vec4(col, 1.0);
}
