void mainImage(out vec4 O, vec2 u )
{
    vec2 U = u / iResolution.xy; 
    O = vec4(1);  
    float N;

    if (U.x < .333) {
        N = 1.,              // "," and pow(float): BUG on Windows 
        O *=  pow( N, 2.);
    }
    else if (U.x < .666) {
        N = 1.;              // ";" and pow(float): no bug
        O *=  pow( N, 2.); 
    }
    else { 
        N = 1.,              // ',' and pow(vec4): no bug
        O *= pow( vec4(N), vec4(2));
    }

    if ( (int(u) % (int(iResolution)/3) ) == 0 ) O=vec4(1,0,0,1); // red separators
}
