void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xx;
    vec3 col = texture(iChannel0,uv).rgb*0.2;
    uv*= 0.2;
    float time = iTime * 0.2;

    col += texture(iChannel0,uv+ 
     vec2(time *.05,0.) +0.5 * texture(iChannel0,uv+ 
     vec2(0.,time *.025) + 0.25 *texture(iChannel0,0.5*uv+
     vec2(time *.0125,-time *.0125) + 0.125 *texture(iChannel0,.25*uv).rg).rb).gb).rgb;

    col = vec3(col.g,col.r,col.b);
    fragColor = vec4(col * col * col * 4.,1.0);
}
