#define time iTime *5.0
#define tau 6.2831853
float random (in vec2 _st) {
            return fract(sin(dot(_st.xy,
                                vec2(12.9898*sin(time),78.233)))*
                43758.5453123);
        }
float noise (in vec2 _st) {
            vec2 i = floor(_st);
            vec2 f = fract(_st);

            // Four corners in 2D of a tile
            float a = random(i);
            float b = random(i + vec2(1.0, 0.0));
            float c = random(i + vec2(0.0, 1.0));
            float d = random(i + vec2(1.0, 1.0));

            vec2 u = f * f * (3.0 - 2.0 * f);

            return mix(a, b, u.x) +
                    (c - a)* u.y * (1.0 - u.x) +
                    (d - b) * u.x * u.y;
        }
mat2 makem2(in float theta){float c = cos(theta);float s = sin(theta);return mat2(c,-s,s,c);}
float fbm(in vec2 p)
{	
	float z=2.;
	float rz = 0.;
	vec2 bp = p;
	for (float i= 1.;i < 6.;i++)
	{
		rz+= abs((noise(p)-0.5)*2.)/z;
		z = z*2.;
		p = p*2.;
	}
	return rz;
}

float dualfbm(in vec2 p)
{
    //get two rotated fbm calls and displace the domain
	vec2 p2 = p*.1;
	vec2 basis = vec2(fbm(p2-time*1.2),fbm(p2+time*1.38));
	basis = (basis-.5)*.2;
	p += basis;

	//coloring
	return fbm(p*makem2(time*0.2));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // center 0
    uv -= 0.5;
    // aspect fix
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 colT = 0.5 + 0.5*cos(time+uv.xyx+vec3(0,2,4));

    // mirror
    uv.x= -1.*abs(uv.x);

    vec4 col = vec4( 1.0);
    vec2 st = vec2(atan(uv.x,uv.y), length(uv));

    float ampl = texture( iChannel0, st ).x;
    float d = length(uv);
    float blur = 0.099*abs(sin(time)/10.+.5)/2.0*noise(st);

    float fm = 1.;//dualfbm(uv*ampl/32.);

    col.r = smoothstep(colT.r+blur,colT.r-0.001+-blur,d*fm);
    col.g = smoothstep(0.3+blur,0.3-0.001-blur,d*fm);
    col.b = smoothstep(colT.b+blur,colT.b-0.001-blur,d*fm);

    col.rgb*= 1.;
    // eyebrow effect
    col.rgb*= 1.*sin(ampl*10.)*3.14;

    // Output to screen
    fragColor = vec4(col);
}
