// 3D version of https://www.shadertoy.com/view/NsKBDw

#define rot(a)       mat2(cos(a+vec4(0,11,33,0)))         // rotation                  

float line(vec3 p, vec3 a,vec3 b) {                       // line SDFÂ²
    p -= a, b -= a;
    p -= b * clamp(dot(p, b) / dot(b, b), 0., 1.); 
    return dot(p,p); 
}
void mainImage(out vec4 O, vec2 U)
{
    float t=9., d; 
    vec3  R = iResolution,
          D = normalize(vec3(U+U, -4.*R.y) - R),          // ray direction
          p = 30./R, q,                                   // marching point along ray 
          M = iMouse.z > 0. ? .5*(iMouse.xyz/R -.5)
               :  vec3( 10,4,0)/1e2*cos(.5*iTime+vec3(0,11,0)); // auto demo

    vec2 P =  3.*cos(iTime+vec2(0,11)) + sin(2.1*iTime+vec2(0,12)), I; // spider location

    for (O=vec4(1); O.x > 0. && t > .01; O-=.01) {        // march scene
        q = p; t=1e2;
        q.yz *= rot(-1. -6.*M.y),                         // rotations
        q.xy *= rot(6.*M.x); 
        if (length(vec3(P,.5)-q) < 6. )                   // sphere box
        for( int k=0; k<25; k++) {                        // get spider legs
            d = dot( I = vec2(k%5,k/5)-2. - fract(P), I);
            d > 1. && d < 4. ? t = min(t, line(q, vec3(I+P,0),vec3(P,1) ) ) : t;
         }
        t = sqrt(t)-.05;                                  // convert to line SDF
        q.xy = fract(q.xy+.5)-.5;                         // grid of points
        t = min(t, length(q)-.02);
        p += .5*t*D;                                      // step forward = dist to obj          
     }
}
