void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0);

    // grid uv at the center of the square
    vec2 gv = fract(uv * 5.) - 0.5;

    float c = 0.;

    for (int y = -1; y <= 1; y++)
    {
        for (int x = -1; x <= 1; x++)
        {
            vec2 offset = vec2(x,y);
            float d = length(gv + offset);

            float a = sin(iTime*2. + length(gv*10.) - length(uv)) * 0.5 + 0.5;
            float r = mix(0.4, 1., a);

            c += smoothstep(r, r-0.01, d);
        }

    }

    col += c;
    col *= vec3(0.8,0.5,0.9);
    col *= 0.5;

    fragColor = vec4(col,1.0);
}
