#define GREEN vec4(0.3, 0.9, 0.1, 1.0)
#define PURPLE vec4(1.0, 0.0, 1.0, 1.0)
#define BLUE vec4(0.0, 0.0, 1.0, 1.0)
#define ORANGE vec4(1.0, 0.5, 0.0, 1.0)
#define YELLOWISH_GREEN vec4(0.5, 1.0, 0.0, 1.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 0.1;

    float width = iResolution.x;
    float height = iResolution.y;

    vec2 c = vec2(0.285, 0.01);
    vec2 z = fragCoord.xy / iResolution.xy;
    z -= 0.5;
    z *= 0.5; 
    int maxIter = int(100.0 + 100.0 * (iTime * 0.1));
    int iter = 0;

    while (iter < maxIter) {
        z = vec2(
    z.x * z.x - z.y * z.y + c.x * sin(time*0.07 + 1.29), 
    2.0 * z.x * z.y + c.y
);
        if (length(z) > 2.0) {
            break;
        }

        iter++;
    }

    if (iter == maxIter) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } else {
        float n = float(iter) / float(maxIter);
        n = smoothstep(0.0, 1.0, n);
        fragColor = mix(GREEN, mix(PURPLE, mix(BLUE, mix(ORANGE, YELLOWISH_GREEN, n), n), n), n);
    } 



}
