float Circle(vec2 uv, vec2 pos, float r1, float r2) {
    float d = length(uv - pos);
    float c = d;
    if(d > r1 && d < r2) c = 0.;
    else c = 1.;
    return c;    
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord.xy / iResolution.xy;
   uv -= .5;
   uv.x *= iResolution.x/iResolution.y;
   float c = Circle(uv, vec2(.0, .0), .2, .215);
   c *= Circle(uv, vec2(.06, .05), .06, .065);  // right eye
   c *= Circle(uv, vec2(-.06, .05), .05, .055); // left eye
   c *= Circle(uv, vec2(-.04, .04), .0, .015); //right pupil
   c *= Circle(uv, vec2(.04, .04), .0, .015); // left pupil
   c *= Circle(uv, vec2(-.005, -.005), .0, .025); // nose
   uv.x /= iResolution.y/(2.*iResolution.x);
   c *= Circle(uv, vec2(.2, 0.35), .15, .165); // right ear
   c *= Circle(uv, vec2(-.2, 0.35), .15, .165); // right ear
   uv.x /= 5.*iResolution.x/iResolution.y;
   c *= Circle(uv, vec2(-.04, -0.23), .03, .035); // left foot
   c *= Circle(uv, vec2(.04, -0.23), .03, .035); // right foot
   uv.x *= 4.*iResolution.x/iResolution.y;
   c *= Circle(uv, vec2(-.68, -0.06), .08, .09); // left arm
   c *= Circle(uv, vec2(.68, -0.06), .08, .09); // right arm
   fragColor = vec4(vec3(c), 1.);
  }
