void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 line = vec3(55.5);

    uv*= 8.;

    float len;

 	for (int i = 0; i < 6; i++) {
        len = length(vec2(uv.x, uv.y));

        uv.x +=  cos(tan(uv.y + iTime - 0.3))*0.02;
        uv.y +=  (sin(uv.x + iTime / 0.4 + tan(len / 2.0)))*0.1;

    }


  	vec3 color = vec3(cos(len + 0.04)*2., tan(len * .6)+4., tan(len - 8.1)+3.);
    vec3 xColor = vec3(sin(color.x/5.),cos(color.x/2.01),cos(color.y*.9));
  //  xColor = - xColor;
    fragColor = vec4(xColor, 0.5);
}
