float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    return length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.) );
}
#define S(v)  smoothstep( 54./R.y,0., v )           // antialiasing

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, A,B=R-R,
          U = 18.*( 2.*u - R ) / R.y;
    O-=O;
    for( float a=-1.; ++a<18.; B=A )
        A = a * cos( a*iTime/5. + vec2(0,11) ),     // dot location
        O +=  S( line(U,A,B) )                      // lines
            + S( length(A-U) -.3 )                  // dots
            + S( abs(length(U)-a) ) /3. ;           // rings


}





