#define row 80.0
#define column 45.0

vec3 Fetch(vec2 pos) {
  float minScale = 0.0;
  float maxScale = 10.0;
  float factor = clamp(minScale,maxScale,iTime*0.1+0.1);
  vec2 res = vec2(row, column)*factor;
  pos=floor(pos*res)/res;
  return texture(iChannel0,pos.xy,-16.0).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = Fetch(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}
