void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.y;
  uv = uv*2.0 - vec2(0.7,0.0);
  float Length = length(uv);

  float blurRadius = 0.2;
  float sunRadius = 0.5;
  vec3 color = vec3(1.0,0.71,0.05);

  Length = 1.0-smoothstep(Length,0.0,sunRadius) / blurRadius;
  fragColor = vec4(color*Length,1.0);
}
