void mainImage( out vec4 o, vec2 p)
{
    float l = iTime+(p*=8./iResolution.y).y,
    d = ceil(l+=sin(l+sin(l/.9))*.5),
    a = d-l-.5;
    o = fract(texture(iChannel0,vec2(d,ceil(p/(cos(d)+2.)+
    sin(d)*iTime))/9.).r+vec4(.3,.7,0,0))*(1.-a*a/.4);
}
