float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float c = 5. + 1. * thc(2., 0.8 * iTime);
    float l = mix(4. * length(uv) * length(uv), 1. / (1. + length(uv)), .5 + .5 * thc(3., iTime));
    uv = vec2(ths(3., c * uv.x * l), ths(3., c * uv.y * l));

    float sc = 64.;
    vec2 fpos = fract(sc * uv) - 0.5;
    vec2 ipos = floor(sc * uv) + 0.5;

    float a = atan(ipos.y, ipos.x);
    float r = log(cos(10. * length(ipos)/sc + 4. * iTime) + thc(3.,30. * length(ipos)/sc + 2. * a + iTime));//log(length(ipos));

    vec2 v = r * vec2(cos(a), sin(a));

    float d = length(v);
    d = clamp(d, 0., 1.);
    float s = step(d,0.74 -.25 * thc(4., iTime));

    vec3 col = vec3(s);

    // Output to screen
    fragColor = vec4(col,1.0);
}
