float rect(vec2 uv, vec2 pos, vec2 size){
    if(uv.x  > pos.x && uv.y > pos.y && uv.x < pos.x + size.x && uv.y < pos.y + size.y){
        return 1.;}
    return 0.;


}

float circle(vec2 uv, vec2 pos, float r, float p){
    vec2 a = pow(abs(uv - pos), vec2(p));
    return smoothstep(r-1., r+1., pow(a.x + a.y, 1./p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 R = iResolution.xy;
    float t = sin(iTime*2.);

    float col = (t / 2. + .5);

    float r = rect(fragCoord, vec2(10., 10.), vec2(R.x / 2.-20., R.y-20.));
    r -= rect(fragCoord, vec2(R.x/2.+10., 10.), vec2(R.x / 2.-20., R.y-20.));

    float c = circle(fragCoord, vec2(R.x/2.-t*50., R.y/3.*2.), 50.*(1.-t), t+1.);
    c -= circle(fragCoord, vec2(R.x/2.+t*50., R.y/3.), 50.*(1.-t), t+1.);
    c -= circle(fragCoord,vec2(R.x/2.+t*50.,R.y/3.*2.),50.*abs(t), (1.-t)+1.);
    c += circle(fragCoord, vec2(R.x/2.-t*50., R.y/3.), 50.*abs(t), (1.-t)+1.);

    col += r;
    col += c;

    fragColor = vec4(vec3(abs(col)/2.), 1.);
}
